import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Form, Button, Alert } from 'react-bootstrap';
import { complaintService } from '../../services/complaintService';
import { useAuth } from '../../contexts/AuthContext';
import ComplaintCard from './ComplaintCard';
import LoadingSpinner from '../common/LoadingSpinner';

const ComplaintList = () => {
  const { user } = useAuth();
  const [complaints, setComplaints] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filters, setFilters] = useState({
    status: '',
    category: '',
    department: '',
    priority: ''
  });
  const [pagination, setPagination] = useState({
    currentPage: 1,
    totalPages: 1,
    total: 0
  });

  useEffect(() => {
    fetchComplaints();
  }, [filters, pagination.currentPage]);

  const fetchComplaints = async () => {
    try {
      setLoading(true);
      const params = {
        ...filters,
        page: pagination.currentPage,
        limit: 10
      };

      // Remove empty filters
      Object.keys(params).forEach(key => {
        if (!params[key]) delete params[key];
      });

      const response = await complaintService.getComplaints(params);
      
      // FIX: Check if response exists and has complaints array
      const complaintsData = response?.complaints || [];
      const totalPages = response?.totalPages || 1;
      const currentPage = response?.currentPage || 1;
      const total = response?.total || 0;
      
      setComplaints(complaintsData);
      setPagination({
        currentPage: currentPage,
        totalPages: totalPages,
        total: total
      });
    } catch (error) {
      setError('Failed to load complaints');
      console.error('Error fetching complaints:', error);
      setComplaints([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (key, value) => {
    setFilters(prev => ({
      ...prev,
      [key]: value
    }));
    setPagination(prev => ({ ...prev, currentPage: 1 }));
  };

  const clearFilters = () => {
    setFilters({
      status: '',
      category: '',
      department: '',
      priority: ''
    });
    setPagination(prev => ({ ...prev, currentPage: 1 }));
  };

  const handlePageChange = (newPage) => {
    setPagination(prev => ({ ...prev, currentPage: newPage }));
  };

  if (loading && complaints.length === 0) {
    return <LoadingSpinner text="Loading complaints..." />;
  }

  return (
    <Container>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Complaints</h2>
        {(user.role === 'student' || user.role === 'staff') && (
          <Button 
            variant="primary" 
            href="/complaints/new"
          >
            + New Complaint
          </Button>
        )}
      </div>

      {/* Filters */}
      <div className="bg-light p-3 rounded mb-4">
        <Row>
          <Col md={3}>
            <Form.Group>
              <Form.Label>Status</Form.Label>
              <Form.Select
                value={filters.status}
                onChange={(e) => handleFilterChange('status', e.target.value)}
              >
                <option value="">All Status</option>
                <option value="pending">Pending</option>
                <option value="in-progress">In Progress</option>
                <option value="resolved">Resolved</option>
                <option value="rejected">Rejected</option>
              </Form.Select>
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group>
              <Form.Label>Category</Form.Label>
              <Form.Select
                value={filters.category}
                onChange={(e) => handleFilterChange('category', e.target.value)}
              >
                <option value="">All Categories</option>
                <option value="academic">Academic</option>
                <option value="hostel">Hostel</option>
                <option value="infrastructure">Infrastructure</option>
                <option value="administration">Administration</option>
                <option value="other">Other</option>
              </Form.Select>
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group>
              <Form.Label>Priority</Form.Label>
              <Form.Select
                value={filters.priority}
                onChange={(e) => handleFilterChange('priority', e.target.value)}
              >
                <option value="">All Priorities</option>
                <option value="low">Low</option>
                <option value="medium">Medium</option>
                <option value="high">High</option>
              </Form.Select>
            </Form.Group>
          </Col>
          <Col md={3}>
            <Form.Group>
              <Form.Label>Department</Form.Label>
              <Form.Control
                type="text"
                value={filters.department}
                onChange={(e) => handleFilterChange('department', e.target.value)}
                placeholder="Filter by department"
              />
            </Form.Group>
          </Col>
        </Row>
        <div className="mt-3">
          <Button variant="outline-secondary" size="sm" onClick={clearFilters}>
            Clear Filters
          </Button>
        </div>
      </div>

      {error && <Alert variant="danger">{error}</Alert>}

      {/* Complaints List */}
      <div className="mb-4">
        {/* FIX: Added safe check for complaints array */}
        {complaints && complaints.length > 0 ? (
          complaints.map(complaint => (
            <ComplaintCard key={complaint._id} complaint={complaint} />
          ))
        ) : (
          <div className="text-center py-5">
            <h5 className="text-muted">No complaints found</h5>
            <p className="text-muted">
              {Object.values(filters).some(f => f) 
                ? 'Try adjusting your filters' 
                : 'Be the first to create a complaint'
              }
            </p>
            {(user.role === 'student' || user.role === 'staff') && (
              <Button variant="primary" href="/complaints/new">
                Create New Complaint
              </Button>
            )}
          </div>
        )}
      </div>

      {/* Pagination */}
      {pagination.totalPages > 1 && (
        <div className="d-flex justify-content-between align-items-center">
          <div>
            Showing {(pagination.currentPage - 1) * 10 + 1} to{' '}
            {Math.min(pagination.currentPage * 10, pagination.total)} of{' '}
            {pagination.total} complaints
          </div>
          <div>
            <Button
              variant="outline-primary"
              size="sm"
              disabled={pagination.currentPage === 1}
              onClick={() => handlePageChange(pagination.currentPage - 1)}
              className="me-2"
            >
              Previous
            </Button>
            <span className="mx-2">
              Page {pagination.currentPage} of {pagination.totalPages}
            </span>
            <Button
              variant="outline-primary"
              size="sm"
              disabled={pagination.currentPage === pagination.totalPages}
              onClick={() => handlePageChange(pagination.currentPage + 1)}
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </Container>
  );
};

export default ComplaintList;
