import React from 'react';
import { Navbar, Nav, Container, Button, Dropdown } from 'react-bootstrap';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
function Navigation() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  // Check if current route is active
  const isActiveRoute = (path) => {
    return location.pathname === path;
  };

  return (
    <Navbar bg="dark" variant="dark" expand="lg" sticky="top">
      <Container>
        <Navbar.Brand as={Link} to="/" className="fw-bold">
          üè´ College Complaint System
        </Navbar.Brand>
        
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        
        <Navbar.Collapse id="basic-navbar-nav">
          {/* Left-aligned navigation items */}
          <Nav className="me-auto">
            {user && (
              <>
                <Nav.Link 
                  as={Link} 
                  to="/dashboard" 
                  className={isActiveRoute('/dashboard') ? 'active fw-bold' : ''}
                >
                  üìä Dashboard
                </Nav.Link>
                
                <Nav.Link 
                  as={Link} 
                  to="/complaints" 
                  className={isActiveRoute('/complaints') ? 'active fw-bold' : ''}
                >
                  üìù Complaints
                </Nav.Link>
                
                {user.role === 'student' && (
                  <Nav.Link 
                    as={Link} 
                    to="/complaints/new" 
                    className={isActiveRoute('/complaints/new') ? 'active fw-bold' : ''}
                  >
                    ‚ûï New Complaint
                  </Nav.Link>
                )}
                
                {user.role === 'admin' && (
                  <Nav.Link 
                    as={Link} 
                    to="/admin" 
                    className={isActiveRoute('/admin') ? 'active fw-bold' : ''}
                  >
                    ‚öôÔ∏è Admin Panel
                  </Nav.Link>
                )}
              </>
            )}
          </Nav>

          {/* Right-aligned navigation items */}
          <Nav>
            {user ? (
              <Dropdown align="end">
                <Dropdown.Toggle 
                  variant="outline-light" 
                  id="user-dropdown"
                  className="d-flex align-items-center"
                >
                  üë§ {user.name}
                </Dropdown.Toggle>

                <Dropdown.Menu>
                  <Dropdown.Header>
                    <div className="fw-bold">{user.name}</div>
                    <div className="small text-muted">{user.email}</div>
                    <div>
                      <span className={`badge ${
                        user.role === 'admin' ? 'bg-danger' : 
                        user.role === 'staff' ? 'bg-warning' : 'bg-primary'
                      }`}>
                        {user.role}
                      </span>
                    </div>
                  </Dropdown.Header>
                  
                  <Dropdown.Divider />
                  
                  <Dropdown.Item as={Link} to="/dashboard">
                    üìä Dashboard
                  </Dropdown.Item>
                  
                  <Dropdown.Item as={Link} to="/complaints">
                    üìù My Complaints
                  </Dropdown.Item>
                  
                  <Dropdown.Divider />
                  
                  <Dropdown.Item onClick={handleLogout} className="text-danger">
                    üö™ Logout
                  </Dropdown.Item>
                </Dropdown.Menu>
              </Dropdown>
            ) : (
              <div className="d-flex gap-2">
                <Button 
                  as={Link} 
                  to="/login" 
                  variant="outline-light"
                  className={isActiveRoute('/login') ? 'active' : ''}
                >
                  Login
                </Button>
                <Button 
                  as={Link} 
                  to="/register" 
                  variant="primary"
                  className={isActiveRoute('/register') ? 'active' : ''}
                >
                  Register
                </Button>
              </div>
            )}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Navigation;
