import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Alert, Table, Badge } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';

function Dashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [recentComplaints, setRecentComplaints] = useState([]);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const complaintsRes = await axios.get('http://localhost:5000/api/complaints');
      const complaints = complaintsRes.data;

      // Calculate stats based on user role
      let userStats = {};
      
      if (user.role === 'student') {
        userStats = {
          total: complaints.length,
          pending: complaints.filter(c => c.status === 'pending').length,
          resolved: complaints.filter(c => c.status === 'resolved').length
        };
      } else if (user.role === 'staff') {
        const assignedComplaints = complaints.filter(c => 
          c.assignedTo && c.assignedTo._id === user._id
        );
        userStats = {
          total: assignedComplaints.length,
          pending: assignedComplaints.filter(c => c.status === 'pending').length,
          inProgress: assignedComplaints.filter(c => c.status === 'in-progress').length,
          resolved: assignedComplaints.filter(c => c.status === 'resolved').length
        };
      } else if (user.role === 'admin') {
        userStats = {
          total: complaints.length,
          pending: complaints.filter(c => c.status === 'pending').length,
          inProgress: complaints.filter(c => c.status === 'in-progress').length,
          resolved: complaints.filter(c => c.status === 'resolved').length
        };
      }

      setStats(userStats);
      setRecentComplaints(complaints.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    }
  };

  const getStatusVariant = (status) => {
    const variants = {
      'pending': 'warning',
      'in-progress': 'info',
      'resolved': 'success',
      'rejected': 'danger'
    };
    return variants[status] || 'secondary';
  };

  return (
    <div>
      <h2>Dashboard</h2>
      <Alert variant="info">
        Welcome back, {user.name}! ({user.role})
      </Alert>

      {/* Statistics Cards */}
      <Row className="mb-4">
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>Total</Card.Title>
              <h3>{stats.total || 0}</h3>
            </Card.Body>
          </Card>
        </Col>
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>Pending</Card.Title>
              <h3 className="text-warning">{stats.pending || 0}</h3>
            </Card.Body>
          </Card>
        </Col>
        {(user.role === 'staff' || user.role === 'admin') && (
          <Col md={3}>
            <Card className="text-center">
              <Card.Body>
                <Card.Title>In Progress</Card.Title>
                <h3 className="text-info">{stats.inProgress || 0}</h3>
              </Card.Body>
            </Card>
          </Col>
        )}
        <Col md={3}>
          <Card className="text-center">
            <Card.Body>
              <Card.Title>Resolved</Card.Title>
              <h3 className="text-success">{stats.resolved || 0}</h3>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      {/* Recent Complaints */}
      <Card>
        <Card.Header>
          <h5 className="mb-0">Recent Complaints</h5>
        </Card.Header>
        <Card.Body>
          {recentComplaints.length > 0 ? (
            <Table responsive>
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Category</th>
                  <th>Status</th>
                  <th>Date</th>
                </tr>
              </thead>
              <tbody>
                {recentComplaints.map(complaint => (
                  <tr key={complaint._id}>
                    <td>
                      <Link to={`/complaints/${complaint._id}`}>
                        {complaint.title}
                      </Link>
                    </td>
                    <td>{complaint.category}</td>
                    <td>
                      <Badge bg={getStatusVariant(complaint.status)}>
                        {complaint.status}
                      </Badge>
                    </td>
                    <td>{new Date(complaint.createdAt).toLocaleDateString()}</td>
                  </tr>
                ))}
              </tbody>
            </Table>
          ) : (
            <p>No complaints found.</p>
          )}
          <div className="text-center">
            <Link to="/complaints" className="btn btn-primary">
              View All Complaints
            </Link>
          </div>
        </Card.Body>
      </Card>
    </div>
  );
}

export default Dashboard;
