import React, { useState, useEffect } from 'react';
import { Card, Row, Col, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { complaintService } from '../../services/complaintService';
import StatsCards from './StatsCards';
import RecentComplaints from './RecentComplaints';

function Dashboard() {
  const { user } = useAuth();
  const [stats, setStats] = useState({});
  const [recentComplaints, setRecentComplaints] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const complaintsRes = await complaintService.getComplaints();
      const complaints = complaintsRes.complaints || [];

      // Calculate stats based on user role
      let userStats = {};
      
      if (user.role === 'student') {
        const myComplaints = complaints.filter(c => c.createdBy._id === user._id);
        userStats = {
          total: myComplaints.length,
          pending: myComplaints.filter(c => c.status === 'pending').length,
          resolved: myComplaints.filter(c => c.status === 'resolved').length
        };
      } else if (user.role === 'staff') {
        const assignedComplaints = complaints.filter(c => 
          c.assignedTo && c.assignedTo._id === user._id
        );
        userStats = {
          total: assignedComplaints.length,
          pending: assignedComplaints.filter(c => c.status === 'pending').length,
          inProgress: assignedComplaints.filter(c => c.status === 'in-progress').length,
          resolved: assignedComplaints.filter(c => c.status === 'resolved').length
        };
      } else if (user.role === 'admin') {
        userStats = {
          total: complaints.length,
          pending: complaints.filter(c => c.status === 'pending').length,
          inProgress: complaints.filter(c => c.status === 'in-progress').length,
          resolved: complaints.filter(c => c.status === 'resolved').length
        };
      }

      setStats(userStats);
      setRecentComplaints(complaints.slice(0, 5));
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return <div className="text-center py-5">Loading dashboard...</div>;
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h2>Dashboard</h2>
        {(user.role === 'student' || user.role === 'staff') && (
          <Link to="/complaints/new" className="btn btn-primary">
            + New Complaint
          </Link>
        )}
      </div>
      
      <Alert variant="info">
        Welcome back, <strong>{user.name}</strong>! ({user.role})
      </Alert>

      <StatsCards stats={stats} userRole={user.role} />
      <RecentComplaints complaints={recentComplaints} />
    </div>
  );
}

export default Dashboard;
