import React, { useState, useEffect } from 'react';
import { Table, Button, Badge, Form, Row, Col, Modal } from 'react-bootstrap';
import { complaintService } from '../../services/complaintService';
import { userService } from '../../services/userService';
import { formatDate, getStatusVariant, capitalizeFirst } from '../../utils/helpers';

const ComplaintManagement = () => {
  const [complaints, setComplaints] = useState([]);
  const [staff, setStaff] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    category: '',
    department: ''
  });
  const [showAssignModal, setShowAssignModal] = useState(false);
  const [selectedComplaint, setSelectedComplaint] = useState(null);
  const [selectedStaff, setSelectedStaff] = useState('');

  useEffect(() => {
    fetchComplaints();
    fetchStaff();
  }, [filters]);

  const fetchComplaints = async () => {
    try {
      const response = await complaintService.getComplaints(filters);
      setComplaints(response.complaints);
    } catch (error) {
      console.error('Error fetching complaints:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchStaff = async () => {
    try {
      const response = await userService.getStaff();
      setStaff(response);
    } catch (error) {
      console.error('Error fetching staff:', error);
    }
  };

  const handleStatusUpdate = async (complaintId, newStatus) => {
    try {
      await complaintService.updateStatus(complaintId, { status: newStatus });
      fetchComplaints();
    } catch (error) {
      console.error('Error updating status:', error);
    }
  };

  const handleAssign = async (complaintId, staffId) => {
    try {
      await complaintService.assignComplaint(complaintId, staffId);
      setShowAssignModal(false);
      fetchComplaints();
    } catch (error) {
      console.error('Error assigning complaint:', error);
    }
  };

  const openAssignModal = (complaint) => {
    setSelectedComplaint(complaint);
    setSelectedStaff(complaint.assignedTo?._id || '');
    setShowAssignModal(true);
  };

  if (loading) {
    return <div>Loading complaints...</div>;
  }

  return (
    <div>
      <div className="d-flex justify-content-between align-items-center mb-4">
        <h3>Complaint Management</h3>
      </div>

      {/* Filters */}
      <Card className="mb-4">
        <Card.Body>
          <Row>
            <Col md={4}>
              <Form.Group>
                <Form.Label>Status</Form.Label>
                <Form.Select
                  value={filters.status}
                  onChange={(e) => setFilters({...filters, status: e.target.value})}
                >
                  <option value="">All Status</option>
                  <option value="pending">Pending</option>
                  <option value="in-progress">In Progress</option>
                  <option value="resolved">Resolved</option>
                  <option value="rejected">Rejected</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group>
                <Form.Label>Category</Form.Label>
                <Form.Select
                  value={filters.category}
                  onChange={(e) => setFilters({...filters, category: e.target.value})}
                >
                  <option value="">All Categories</option>
                  <option value="academic">Academic</option>
                  <option value="hostel">Hostel</option>
                  <option value="infrastructure">Infrastructure</option>
                  <option value="administration">Administration</option>
                  <option value="other">Other</option>
                </Form.Select>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group>
                <Form.Label>Department</Form.Label>
                <Form.Control
                  type="text"
                  value={filters.department}
                  onChange={(e) => setFilters({...filters, department: e.target.value})}
                  placeholder="Filter by department"
                />
              </Form.Group>
            </Col>
          </Row>
        </Card.Body>
      </Card>

      {/* Complaints Table */}
      <Card>
        <Card.Body>
          <Table responsive striped>
            <thead>
              <tr>
                <th>Title</th>
                <th>Created By</th>
                <th>Category</th>
                <th>Department</th>
                <th>Status</th>
                <th>Assigned To</th>
                <th>Created Date</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {complaints.map(complaint => (
                <tr key={complaint._id}>
                  <td>
                    <strong>{complaint.title}</strong>
                  </td>
                  <td>{complaint.createdBy.name}</td>
                  <td>
                    <Badge bg="secondary">{complaint.category}</Badge>
                  </td>
                  <td>{complaint.department}</td>
                  <td>
                    <Badge bg={getStatusVariant(complaint.status)}>
                      {capitalizeFirst(complaint.status)}
                    </Badge>
                  </td>
                  <td>
                    {complaint.assignedTo ? (
                      complaint.assignedTo.name
                    ) : (
                      <span className="text-muted">Not assigned</span>
                    )}
                  </td>
                  <td>{formatDate(complaint.createdAt)}</td>
                  <td>
                    <div className="d-flex gap-1">
                      <Button
                        size="sm"
                        variant="outline-primary"
                        onClick={() => openAssignModal(complaint)}
                      >
                        Assign
                      </Button>
                      <Button
                        size="sm"
                        variant="outline-success"
                        onClick={() => handleStatusUpdate(complaint._id, 'resolved')}
                        disabled={complaint.status === 'resolved'}
                      >
                        Resolve
                      </Button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>

          {complaints.length === 0 && (
            <div className="text-center text-muted py-4">
              No complaints found matching the filters.
            </div>
          )}
        </Card.Body>
      </Card>

      {/* Assign Modal */}
      <Modal show={showAssignModal} onHide={() => setShowAssignModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Assign Complaint</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form.Group>
            <Form.Label>Select Staff Member</Form.Label>
            <Form.Select
              value={selectedStaff}
              onChange={(e) => setSelectedStaff(e.target.value)}
            >
              <option value="">Select staff member...</option>
              {staff.map(staffMember => (
                <option key={staffMember._id} value={staffMember._id}>
                  {staffMember.name} - {staffMember.department}
                </option>
              ))}
            </Form.Select>
          </Form.Group>
          {selectedComplaint && (
            <div className="mt-3">
              <strong>Complaint:</strong> {selectedComplaint.title}
            </div>
          )}
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowAssignModal(false)}>
            Cancel
          </Button>
          <Button 
            variant="primary" 
            onClick={() => handleAssign(selectedComplaint._id, selectedStaff)}
            disabled={!selectedStaff}
          >
            Assign
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

export default ComplaintManagement;
